package Sirisha_JavaFinalPractice;

public class Loop_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*For -if loop:
			syntax:

			for(initialization,condn,incrment/decrement)
			if (cond)
			print
			else
			print




			(II)*/
			
			for(int i=1;i<=100;i++)
			{
			if(i%2==0){

			System.out.println(i+"is even");
			}

			else
			System.out.println(i+"is odd");


	}

}
}