package Sirisha_JavaFinalPractice;

public class While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*While :check the conditon first then printing
		syntax 
		While(condn)
		{
		code---execute
		increment thdecrement---perform 
		}*/


		//createa
		
		int i=1;
		while(i<=100)
		{
		System.out.println(i);
		i++;
		}
		System.out.println("While loop is executed ");


		
	}

}
